
INSERT INTO educational_tips (title, content, category, created_at, updated_at) VALUES
('How to Spot Deepfake Videos', 'Look for unnatural facial expressions, inconsistent lighting, mismatched lip sync, and unusual blinking patterns. Deepfakes often struggle with rendering natural eye movements and may show artifacts around the face edges.', 'deepfakes', datetime('now'), datetime('now')),

('Detecting Misleading Headlines', 'Check if the headline matches the article content. Look for sensational language, ALL CAPS text, or clickbait phrases. Compare headlines across multiple sources to identify potential exaggeration or misrepresentation.', 'misleading_headlines', datetime('now'), datetime('now')),

('Verifying Source Credibility', 'Research the publication''s history, check their About page, verify contact information, and look for editorial standards. Cross-reference information with established fact-checking organizations like Snopes, FactCheck.org, or PolitiFact.', 'source_verification', datetime('now'), datetime('now')),

('Identifying Bias and Manipulation', 'Watch for emotional language, cherry-picked statistics, missing context, and one-sided reporting. Look for loaded words, false dichotomies, and appeals to emotion rather than factual evidence.', 'bias_detection', datetime('now'), datetime('now')),

('Reverse Image Search Techniques', 'Use Google Images, TinEye, or Bing Visual Search to find the original source of images. This helps identify if photos have been taken out of context, doctored, or are from a completely different event.', 'source_verification', datetime('now'), datetime('now')),

('Checking Video Metadata', 'Examine creation dates, location data, and technical specifications. Inconsistent metadata can indicate content manipulation. Tools like InVID WeVerify can help analyze video authenticity.', 'deepfakes', datetime('now'), datetime('now'));
